import React from 'react';
import {
  Image,
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
  FlatList,
} from 'react-native';
import HeaderComponent from '../../components/HomeComponent/HeaderComponent';
import FooterComponent from '../../components/HomeComponent/FooterComponent';
import {ScrollView} from 'react-native-virtualized-view';

interface Category {
  id: string;
  name: string;
  image: string; // URL ảnh
}
interface Shop {
  id: string;
  name: string;
  location: string;
  rating: string;
  image: string;
  offers?: Offer[];
  products?: Product[];
}
interface Offer {
  id: string;
  description: string;
  product: Product[];
  image: string;
}
interface Product {
  id: string;
  name: string;
  description: string;
  price: string;
  category: Category;
  images: string[];
}


const HomeScreen = ({navigation}: any) => {
  const renderCategoryItem = ({item}: any) => (
    <TouchableOpacity
      style={styles.category}
      onPress={() => {
        navigation.navigate('CategoryDetail', {category: item});
      }}>
      <Image source={item.image} style={styles.categoryImage} />
      <Text style={styles.categoryText}>{item.name}</Text>
    </TouchableOpacity>
  );

  const renderProductItem = ({item}: any) => {
      
    return (
      <TouchableOpacity
        style={styles.dishContainer}
        onPress={() => {
          navigation.navigate('ProductDetail', {product: item});
        }}>
           
            <Image
              source={{ uri: item.images[0] }}
              style={styles.dishImage}
            />
          
        <View style={styles.dishInfo}>
          <Text style={styles.dishName}>{item.name}</Text>
          <Text style={styles.dishDescription}>{item.description}</Text>
          <Text style={styles.dishPrice}>{item.price}</Text>
        </View>
      </TouchableOpacity>
    );
  };
  const renderOfferItem = ({item}: any) => (
    <TouchableOpacity
      style={styles.offerContainer}
      onPress={() => {
        navigation.navigate('OfferDetail', {offer: item});
      }}>
      <Image source={item.image} style={styles.offerImage} />
      {/*<View style={styles.offerInfo}>
        <Text style={styles.offerDescription}>{item.description}</Text>
  </View>*/}
    </TouchableOpacity>
  );

  const renderShopItem = ({item}: any) => (
    <TouchableOpacity
      style={styles.shopContainer}
      onPress={() => {
        navigation.navigate('ShopDetail', {shop: item});
      }}>
      <View style={styles.shopImageContainer}>
        <Image source={item.image} style={styles.shopImage} />
      </View>
      <View style={styles.shopInfo}>
        <Text style={styles.shopName}>{item.name}</Text>
        <Text style={styles.shopLocation}>{item.location}</Text>
        <Text style={styles.shopRating}>{item.rating} stars</Text>
      </View>
    </TouchableOpacity>
  );

  return (
    <ScrollView showsVerticalScrollIndicator={false} style={styles.container}>
      <View style={{flex: 1, marginBottom: 10}}>
        <Text style={styles.title}>Good Evening Luisa</Text>
        <HeaderComponent />
        <View style={{flex: 1}}>
          <View style={styles.categoriesContainer}>
            <Text style={styles.sectionTitle}>Categories</Text>
            <FlatList
              horizontal
              data={categories}
              renderItem={renderCategoryItem}
              keyExtractor={item => item.id}
              showsHorizontalScrollIndicator={false}
            />
          </View>

          <View style={styles.offersContainer}>
            <Text style={styles.sectionTitle}>Special Offers</Text>
            <FlatList
              horizontal
              data={offers}
              renderItem={renderOfferItem}
              keyExtractor={item => item.id}
              showsHorizontalScrollIndicator={false}
            />
          </View>

          <View style={styles.popularContainer}>
            <Text style={styles.sectionTitle}>Popular Dishes</Text>
            <FlatList
              data={products}
              renderItem={renderProductItem}
              keyExtractor={item => item.id}
            />
          </View>

          <View style={styles.shopsContainer}>
            <Text style={styles.sectionTitle}>Featured Shops</Text>
            <FlatList
              data={shops}
              renderItem={renderShopItem}
              keyExtractor={item => item.id}
              numColumns={2} 
              columnWrapperStyle={styles.columnWrapper}
              showsHorizontalScrollIndicator={false}
            />
          </View>
        </View>
      </View>
    </ScrollView>
  );
};

export default HomeScreen;

